const express = require('express');
const mysql = require('mysql2');
const app = express();
const port = 3000;

// إعداد اتصال MySQL باستخدام اسم خدمة الـ Docker 'db'
const db = mysql.createConnection({
  host: 'db', 
  user: 'user', 
  password: 'password', 
  database: 'healthpal' 
});

db.connect((err) => {
  if (err) {
    console.error('Error connecting to MySQL: ', err.message);
    return;
  }
  console.log('Connected to MySQL service successfully! The Backend is ready.');
});

// راوتر بسيط لاختبار الاتصال
app.get('/', (req, res) => {
  db.query('SELECT "Node.js is connected to Docker MySQL" AS message, NOW() AS serverTime', (err, results) => {
    if (err) {
      console.error(err);
      return res.status(500).json({ error: 'Database Query Error' });
    }
    res.json(results[0]);
  });
});

app.listen(port, () => {
  console.log(`Server running inside Docker on http://localhost:${port}`);
});